function chunkArrayInGroups(arr, size) {
  // Break it up.


//We need to create an empty array and a count starting at 0
var newArray = [];
var count = 0;


// The while loop goes through the array and slices them into segments into the newArray
while (count <arr.length){
  // This slices index 0 +1, then 2+3, 3+4 etc etc.
  // Size is dependant on the given number (second argument to the function)
  newArray.push(arr.slice(count,count+size))
  count = count+size
}


  return newArray;
}
// How this basically works will be:

// ["a", "b"] ["c", "d"]
chunkArrayInGroups(["a", "b", "c", "d"], 2);
