function factorialize(num) {
  // if the number gets to 0 (base case), return 1
  if (num ===0){
    return 1;
  // if not, return num * num until it reaches the base case
  }else {
  return num * factorialize(num-1);
}
}
// basic case of recursion 
// so this will be 5 * (5-1) = 20 
//                is num 0? no. then go again
//                 4 * (4-1) = 60
//                is num 0? no. then go again
//                 3 * (3-1) = 120
//                is num 0? no. then go again
//                 2 * (2-1) = 120
//                is num 0? no. then go again
//                 1 * (1-1) ==> BASE CASE HAS BEEN REACHED 
//                 Therefore returns 1 = 120

factorialize(5);

// 5 * 4 * 3 * 2 * 1 =  120
