function bouncer(arr) {
  // Don't show a false ID to this bouncer.

//make new empty array to return 
var newArray = [];

//this returns the true of false value of each element in an array
function removeFalseValues(value) {
  return Boolean(value);
}
//new array now filters through each element of an array giving it a true or false value
newArray = arr.filter(removeFalseValues)

//this is then returns an array with only truthy values
  return newArray;
}

