function findElement(arr, func) {
  // num filters through each element of the array
  // applying the given function to each index
  let num = arr.filter(func);
  // this scouts the FIRST element that matches TRUE and returns only it
  if(num.length > 1){
    return num[0];
  }
  //if no elements on the array match, return undefined.
return undefined;
}

findElement([1, 2, 3, 4], num => num % 2 === 0);
