function repeatStringNumTimes(str, num) {
//Empty Array for str to go into
var placeHolder = []
//Iterates through the str (num) many times
for (var i = 0; i < num; i++){
  //pushes the string into the placeholder array 
  placeHolder.push(str);
}
//str actually equalls the array with the joined string elements
str = placeHolder.join('');


  return str;
}

repeatStringNumTimes("abc", 0);
