function largestOfFour(arr) {
  // empty array for us to return at the end
var placeHolder = [];
// goes through each element of the array
for (var i = 0; i < arr.length; i++){
  //edge case here, so we can return neg numbers up to -100. Could be -100000
  var largestNumber = -100;
  // goes through the array in the array
  for (var j =0; j <arr.length; j++){
    // since largestnumber is -100, it will return a new array of whatever the largest number is.
    if (largestNumber < arr[i][j]){
      largestNumber = arr[i][j];
    }
  }
  // this then gets pushed to the original placeholder array we made at the start
  placeHolder.push(largestNumber)
}

// this will return [5,27,39,1001] as a new array.
  return placeHolder;
}

largestOfFour([[4, 5, 1, 3], [13, 27, 18, 26], [32, 35, 37, 39], [1000, 1001, 857, 1]]);
