function frankenSplice(arr1, arr2, n) {
// Makes a new array that slices the second array from the 0th index for the length of the index
let NewArr = arr2.slice(0,arr2.length)
// then splices in the first array from the n index to the second array
 NewArr.splice(n, 0, ...arr1);

return NewArr;
}
 // This will return [4,1,2,3,5,6]
 // 1 is the index of the array to start the splice
 // 4 (0th index) is sliced, and then [1,2,3] is added before [5,6]
frankenSplice([1, 2, 3], [4, 5, 6], 1);
