function titleCase(str) {
//Splits the string into an array of letters
var stringToArray = str.split(' ');

for (var i=0; i< stringToArray.length; i++){
  var placeHolder = stringToArray[i];
  //upLetter takes the first letter and turns it to uppercase
  var upLetter = placeHolder.charAt(0).toUpperCase();
  // placeHolder then slices the first capitalised letter
  // and then turns the remaining letters to lower case
 placeHolder = placeHolder.slice(1,placeHolder.length).toLowerCase();
 // this then joins the previously separated uppercase letter back to the lower case letters
stringToArray[i] = upLetter.concat(placeHolder);
}
// we then join the array of strings back together as the str argument.
str = stringToArray.join(' ');

  return str;
}

titleCase("I'm a little tea pot");
