function truncateString(str, num) {
 // if the string greater is less than num AND num is greater than 3
 // return the string, sliced by num, with ... concatenated
  if (str.length > num && num > 3) {
    return str.slice(0, num ) + '...';
    // if string is greater than num AND num is less than or equal to 3
    // returns the string, sliced by num with ...
  } else if (str.length > num && num <= 3) {
    return str.slice(0, num) + '...';
    // if no conditions are met, returns the given string.
  } else {
    return str;
  }

}


truncateString("A-tisket a-tasket A green and yellow basket", 8);
