function getIndexToIns(arr, num) {
  // Find my place in this sorted array.

  // set a count variable to zero as a base 
  var count = 0;


// iterates through the array, asking IF each index of the array, subtracted by the given num is less than zero (so if its a negative number)
// count then +1's
  for ( var i = 0; i< arr.length; i++){
  if ((arr[i] - num) <0){
    count++
  }
}

// return the count at the index at which it will stop
  return count;
}
// here count will be 1, as it passes over 40, but stops before 50.
getIndexToIns([40, 60], 50);
